    private List<Cliente> lista = new ArrayList<Cliente>();//Se inicializa nueva lista de clientes


    private void botonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonGuardarActionPerformed
        Cliente client = new Cliente(); //OBJETO
        client.setNombre(this.cajaNombre.getText()); //Se envia el valor ingresado de la caja de texto
        client.setApellido(this.cajaApellido.getText());//Se envia el valor ingresado de la caja de texto
        client.setEmail(this.cajaEmail.getText());//Se envia el valor ingresado de la caja de texto
        client.setTelefono(this.cajaTelefono.getText());//Se envia el valor ingresado de la caja de texto

        lista.add(client); //Se va almacenando el objeto en el arreglo dinamico
        actualizarLista();
        JOptionPane.showMessageDialog(rootPane, "Cliente ingresado correctamente");
        limpiarCajasDeTexto();
    }//GEN-LAST:event_botonGuardarActionPerformed

    private void botonEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonEliminarActionPerformed
        int indice = this.listaClientes.getSelectedIndex(); //Devuelve la posicion del nombre seleccionado de la lista
        lista.remove(indice); //Se elimina el nombre
        actualizarLista();
        JOptionPane.showMessageDialog(rootPane, "Se elimino correctamente");
    }//GEN-LAST:event_botonEliminarActionPerformed

