    /**
     * Metodo que actualiza los nombres de la lista dependiendo si se agregan o
     * eliminan
     */
    private void actualizarLista() {
        DefaultListModel datos = new DefaultListModel(); //Para agregar elementos en la lista, se debe usar este Framework
        for (int i = 0; i < lista.size(); i++) { //Termina cuando llegue al tamaño del arreglo dinamico
            Cliente client = lista.get(i); //Agarramos los valores del cliente en la posicion del iterador
            datos.addElement(client.getNombreCompleto()); //Agregamos el cliente en el objeto de ListModel
        }
        this.listaClientes.setModel(datos);//Modelo de datos
    }

    /**
     * Metodo que se encarga de borrar el texto que el usuario ingreso en las cajas de
     * texto
     */
    private void limpiarCajasDeTexto() {

        this.cajaApellido.setText(""); //Caja de texto vacio
        this.cajaEmail.setText(""); //Caja de texto vacio
        this.cajaNombre.setText(""); //Caja de texto vacio
        this.cajaTelefono.setText(""); //Caja de texto vacio

    }

